<script>
import { mapActions } from 'vuex';
import axios from 'axios'
import NavBar from '@/components/NavBar.vue';
import FooTer from '@/components/FooTer.vue';
export default {
    components: { NavBar, FooTer },
    data(){
        return{
            user:{
                username:'',
                email:'',
                password:'',
                adresse:'',
                date_de_naissance:''
                
            }
        }
    },
    methods:{
        
        async  signup(){
            var form=new FormData();
            form.append('username',this.user.username)
            form.append('email',this.user.email)
            form.append('password',this.user.password)
            form.append('adresse',this.user.adresse)
            form.append('date_de_naissance',this.user.date_de_naissance)
            let res=await axios({
                method:'POST',
                url:'http://localhost/BACKFISH/Register/signUp',
                data:form,
                Headers:{
                    "Content-Type": "multipart/form-data"
                }
            })
            if(res.data.message=="Inscription reussi"){
                alert('Added new user successfully')
                this.$router.push('/login')
            }else{
                console.log('Error on sign up')
            }
        }
    }
}
</script>

